/**
/*
* 
* @description: This component contains medicine list in the dashboard view
* @author: Yuchen Cao
* @update: Yuchen Cao (2022-11-3 11:16)
*
*/

<template>
  <div class="list_containder">
    <!-- <h1> {{ this.title }}</h1> -->
    <div class="list_item_container">
    <!-- <img src="../assets/logo.png" alt=""> -->
      <div class="list_header_line" v-if="!isPhone">
        <div class="list_header">Medicine Name</div>
        <div class="list_header">Photo</div>
        <div class="list_header">quantity</div>
        <div class="list_header">Validity &nbsp; DD/MM/YYYY</div>
        <div class="list_header">Dosage</div>
      </div>
      <div class="desktop_list_warpper" v-if="!isPhone">
        <div class="medi_row" 
          v-for="medi_data in this.listData" 
          :key='medi_data.id'
          @click="goToMediDetail(medi_data.id)"  
        >
          <div class="medi_info">
            {{medi_data.brandname}}
          </div>
          <div class="medi_info">
            <div>
              <img class="medi_photo" :src="medi_data.image">
            </div>
          </div>
          <div class="medi_info">
            {{medi_data.quantity}} {{medi_data.quantitytype}}
          </div>
          <div class="medi_info valid" v-if="this.isValid(medi_data.validity)">
            {{medi_data.validity}}
          </div>
          <div class="medi_info invalid_medicine" v-if="!this.isValid(medi_data.validity)">
            {{medi_data.validity}}
          </div>
          <div class="medi_info">
            {{medi_data.dosage}} {{medi_data.dosagetype}}
          </div>
        </div>
      </div>

      <div class="phone_list_warpper" v-if="isPhone">
        <div 
          class="medi_row" 
          v-for="medi_data in this.listData" 
          :key='medi_data.id' 
          @click="goToMediDetail(medi_data.id)"
        >
          <div class="medi_info_left">
            {{medi_data.brandname}}
            <div>
              <img class="medi_photo" :src="medi_data.image">
            </div>
          </div>

          <div class="medi_info_right">
            <table>
              <tr>
                <td style="text-align=left">
                  Quantity: 
                </td>
                <td style="text-align=right">
                  {{medi_data.quantity}} {{medi_data.quantitytype}}
                </td>
              </tr>
              <tr>
                <td style="text-align=left">
                  Validity: 
                </td>
                <td style="text-align=right" v-if="this.isValid(medi_data.validity)">
                  {{medi_data.validity}}
                </td>
                <td style="text-align=right" class="invalid_medicine" v-if="!this.isValid(medi_data.validity)">
                  {{medi_data.validity}}
                </td>
              </tr>
              <tr>
                <td style="text-align=left">
                  Dosage: 
                </td>
                <td style="text-align=right">
                  {{medi_data.dosage}} {{medi_data.dosagetype}}
                </td>

              </tr>
            </table>

          </div>
        </div>
      </div>

    </div>



    <div class="page_select_bar" v-if="!isSearchResult">
      <MedicineListPagination :total = "this.total" :pagesize = "pagesize" :page="this.page" :totalPages = "this.totalPages" @change = "changePage" />
    </div>
  </div>
</template>


<script>
import MedicineListPagination from './MedicineListPagination.vue';
import api from '../api/index'





export default {
    name: "MedicineList",
    data() {
        return {
        //display_flag : true,
        isPhone : null,
        pagesize : 5,
        page : 1,
        totalPages : Math.ceil(this.total/this.pagesize),
        title : "My Medicines",
        imageBasePath : "https://medione.herokuapp.com/",
        listData : [],
        // [
        //   {
        //     id: 1,
        //     brandname: "Norvasc" + 1,
        //     image: require("../assets/logo.png"),
        //     quantity: 2,
        //     validity: "03/08/2022",
        //     dosage: 3,
        //     dosagetype: "pills",
        //     quantitytype: "bottles",
        //   },
        //   {
        //     id: 2,
        //     brandname: "Norvasc" + 2,
        //     image: require("../assets/logo.png"),
        //     quantity: 3,
        //     validity: "03/08/2022",
        //     dosage: 4,
        //     dosagetype: "pills",
        //     quantitytype: "bottles",
        //   },
        //   {
        //     id: 3,
        //     brandname: "Norvasc" + 3,
        //     image: require("../assets/logo.png"),
        //     quantity: 3,
        //     validity: "03/08/2022",
        //     dosage: 4,
        //     dosagetype: "drops",
        //     quantitytype: "boxes",
        //   },
        //   {
        //     id: 4,
        //     brandname: "Norvasc" + 4,
        //     image: require("../assets/logo.png"),
        //     quantity: 3,
        //     validity: "03/08/2022",
        //     dosage: 4,
        //     dosagetype: "capsules",
        //     quantitytype: "packs",
        //   },
        //   {
        //     id: 5,
        //     brandname: "Norvasc" + 5,
        //     image: require("../assets/logo.png"),
        //     quantity: 3,
        //     validity: "03/08/2022",
        //     dosage: 4,
        //     dosagetype: "capsules",
        //     quantitytype: "packs",
        //   }
        // ]
        }
    },
    props:{
      total: {
        type: Number,
        default: 0,
      },
      isSearchResult:{
        type: Boolean,
        default : false,
      },
      searchResult : {
        type : Object,
        default : null,
      }
    },

    components: { 
      MedicineListPagination 
    },
    created(){
      if(this.isSearchResult){
        console.log("Display Search Result")
        this.title = "Search Result"
        this.listData = this.searchResult
      }
      else{
        console.log("Getting pagination data")
        this.getPageData(1, this.pagesize)
      }
      this.isPhone = !(window.innerWidth > 992);

    },
    // beforeMount(){
    //   this.isPhone = !(window.innerWidth > 992);
    // },
    methods: {
      changePage(page, pagesize) {
         var _page = this.page,
             _pagesize = this.pagesize;
        this.page = page;
        this.pagesize = pagesize;
        if (page !== _page && pagesize || _pagesize !== pagesize){
          console.log("get list data")
          this.getPageData(page, pagesize)
        } 
      },

      getPageData(curPage, pageSize){
        const loading = this.openFullScreen2()
        console.log("Getting page " + curPage + " data")
        var that = this
        api.getPageData(curPage, pageSize).then(res => {
          loading.close()
          console.log("Pagination data received")
          console.log(res.data)
          that.listData = res.data.data.records
        })
      },


      goToMediDetail(id){
        var mode = {
          view : 'medi_info',
          mediId : id
        }
        this.$emit("switch-event", mode)
      },
      convertDate(date){
        var dateList = date.split('/')
        var day = dateList[0]
        var month = dateList[1]
        var year = dateList[2]
        return year + "-" + month + "-" + day
      },

      isValid(date) {
        var sDate1 = this.convertDate(date)
        let dateSpan,iDays
        sDate1 = Date.parse(sDate1)
        var sDate2 = new Date()
        dateSpan = sDate1 - sDate2
        //dateSpan = Math.abs(dateSpan)
        iDays = Math.floor(dateSpan / (24 * 3600 * 1000))
        return iDays >= 15
      },

      openFullScreen2(){
        const loading = ElLoading.service({
          lock: true,
          text: 'Loading',
          background: 'rgba(0, 0, 0, 0.7)',
        })
        // setTimeout(() => {
        //   loading.close()
        // }, 10000)
        return loading
      }

  }
}
</script>

<style scoped>

@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@400;700&display=swap');

* {
  font-family: "Poppins", sans-serif;
}

@media screen and (max-width: 992px){   
  list_item_container{
    width: 100%;
    padding: 2em;
  }

  .medi_photo{
    width: 5em;
    height: 5em;
  }


  .medi_row{
    width: 100%;
    display: flex;
    align-items: center;
    margin-top: 1em;
    margin-bottom: 1em;
    border: 1px solid #dadcf4;
    border-radius: 1.5em;
    background-color: #f2f6ff;
    box-shadow: 2px 2px 2px #b8cbdd;
    font-size: 1em;
    transition-duration: 0.3s;
  }

  .medi_row:hover{
    background-color: #dfe8fc;
    box-shadow: 12px 12px 12px #a9bfd3
  }

  .medi_info_left{
    width: 35%;
    height: 100%;
    text-align: left;
    font-size: 1em;
    color: #3082CC;
    padding-left: 1em;
    font-weight: 600;
  }
  .medi_info_right{
    width: 65%;
    height: 100%;
    text-align: center;
    font-size: 0.8em;
    color : #3082CC;
  }

  .desktop_list_warpper{
    margin: 0;
    padding: 0;
    border: 0;
    width: 100%;
  }

  .invalid_medicine{
    color: #000000;
    background-color: #FF4D4D;
    border-radius: 2em;

  }
}






@media screen and (min-width: 992px){   
  list_item_container{
    width: 100%;
    padding: 2em;
  }

  .medi_photo{
    width: 6em;
    height: 100%;
  }

  .list_header_line{
    width: 100%;
    display: flex;
    text-align: center;
  }

  .list_header{
    width : 20%;
    font-size: 1.3em;
    color: #6E78F7;
    font-weight: 600;
  }

  .medi_row{
    width: 100%;
    height: 8em;
    display: flex;
    align-items: center;
    margin-top: 1em;
    margin-bottom: 1em;
    border: 1px solid #dadcf4;
    border-radius: 1.5em;
    background-color: #f2f6ff;
    box-shadow: 2px 2px 2px #b8cbdd;
    transition-duration: 0.3s;
  }

  .medi_row:hover{
    background-color: #dfe8fc;
    box-shadow: 12px 12px 12px #a9bfd3
  }

  .medi_info{
    width: 20%;
    /*height: 100%;*/
    text-align: center;
  }

  .invalid_medicine{
    color: #ffffff;
    background-color: #FF4D4D;
    border-radius: 2em;
    padding: 1em;
  }
  .valid{
    padding: 1em
  }
}

</style>
